import React from 'react'
import { useRef, useState } from 'react';
import { API_CLIENT } from '../../shared/services/api_client';
import {useNavigate} from 'react-router-dom';


const Details = () => {
    
    
    const history = useNavigate();
    const [msg,setMsg] =useState('');
    const name = useRef('');
    const age = useRef('');
    const gender = useRef('');
    const area = useRef('');
    const extra_info = useRef('');



    const adddetails = async (e)=>{
        e.preventDefault();
        
        const name1 = name.current.value;
        const age1= age.current.value;
        const gender1 = gender.current.value;
        const area1 = area.current.value;
        const extra1 = extra_info.current.value;

        const requestObject = {'userid':name1, 'password':age1 , 'email': gender1 , 'phone':area1 , 'extra':extra1};
        console.log('requestObject is ', requestObject);
        try{
        const result = await API_CLIENT.post("http://localhost:4321/request", requestObject);    
        setMsg(result.data.message);    
    }
        catch(err){
            console.log('Error in detailsadd ', err);
        }

    }

  return (
    <div>
        <h1 id='details'>Details</h1>
        <h4>{msg}</h4>
        <form action="">
        <div className="container1">
        <div className="input">
            <ul>
                <li>
                    <label>User id</label>
                    <input type="text" ref={name}  placeholder="Enter name"/>
                </li>
                <br />
                <li>
                    <label>Password</label>
                    <input type="password" ref={age} placeholder="Enter age"/>
                </li>
                <br />
                <li>
                    <label>Email</label>
                    <input type="text" ref={gender} placeholder="Enter the gender of child"/>
                </li>
                <br />
                <li>
                    <label>Phone no.</label>
                    <input type="text" ref={area} placeholder="Enter the area "/>
                </li>

                <li>
                    <label>Phone no.</label>
                    <input type="text" ref={extra_info} placeholder="Enter extra infomation"/>
                </li>
                <br />
                <li>
                    <button onClick ={adddetails}  >Add Details</button>

                </li>

            </ul>
                           
        </div>
                        
                        
    </div>
            
        
        </form>
      
    </div>
  )
}

export default Details;
